    public function login($html)
    {
        $form = $html->find('.new_suser', 0);
        $data['authenticity_token'] = $form->find('[name="authenticity_token"]', 0)->getAttribute('value');
        $data['utf8'] = $form->find('[name="utf8"]', 0)->getAttribute('value');
        $data['suser[client_number]'] = $form->find('[name="suser[client_number]"]', 0)->getAttribute('value');//PLZ006087
        $data['suser[login]'] = 'lcparts2';
        $data['suser[password]'] = '123456789';
        $data['suser[admin]'] = $form->find('[name="suser[admin]"]', 0)->getAttribute('value');
        $data['suser[admin_password]'] = $form->find('[name="suser[admin_password]"]', 0)->getAttribute('value');
        $data['commit'] = $form->find('[name="commit"]', 0)->getAttribute('value');
        $result = get_html('http://webcatalog.opoltrans.com.pl/pl/shop/operator/login', $data);
        $html = str_get_html($result[content]);
        if (!is_object($html)) {
            echo "problem is_object - 2";
            exit;
        }
        return $html;
    }
